import React, { useState } from 'react';
import { Button, FormControl, Select, MenuItem, Box, Typography, Grid } from '@mui/material';

function Form({ metadata, onSubmit }) {
  const [formData, setFormData] = useState({
    CODE_DECLARANT: '',
    CODE_OPERATEUR: '',
    PROVENANCE: '',
    CODE_NATURE_COLIS: '',
    CODE_PORT_CHARG: '',
    IDEN_MOY_TRANSP_ARRIVE: '',
    COD_BANQUE: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1, width: '100%' }}>
      {Object.entries(metadata).map(([key, values]) => (
        <Grid container spacing={2} alignItems="center" key={key}>
          <Grid item xs={4}>
            <Typography textAlign="left">{key}</Typography>
          </Grid>
          <Grid item xs={8}>
            <FormControl fullWidth margin="dense">
              <Select
                name={key}
                value={formData[key]}
                onChange={handleChange}
                required
                size="small"
              >
                {values.map((val) => (
                  <MenuItem key={val} value={val}>
                    {val}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        </Grid>
      ))}
      <Button type="submit" variant="contained" color="primary" sx={{ mt: 2 }}>
        Soumettre
      </Button>
    </Box>
  );
}

export default Form;
